import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Link, Routes } from 'react-router-dom';
import './App.css';
import axios from 'axios';


function App() {
  //로그인 쿠키 저장예정
  const [sessionCookie, setSessionCookie] = useState(null)
  const [ID, setID] = useState("")
  const [card, setCard] = useState({})

  const userbar = () =>{
    if(ID != "")
      return ID
    else
    return (<><Link to="/join">회원가입</Link>
      {" "}
      <Link to="/login">로그인</Link></>)
  }

  return (
    <BrowserRouter>
      <nav>
        <div className='home'>
          <Link to="/">Capstone Bank</Link> 
        </div>
        <Menu/>
        <div className='loginButton'>
          {userbar()}
        </div>
      </nav>
      <main>
      </main>
      <Routes>
        <Route path="/" element={<Home cookie={sessionCookie}/>} />
        <Route path="/join" element={<Join/>} />
        <Route path="/login" element={<Login setSessionCookie={setSessionCookie} setID={setID}/>} />

        <Route path="/account" element={<View sessionCookie={sessionCookie}/>}/>
        <Route path="/transfer" element={<Transfer sessionCookie={sessionCookie} />} />
        <Route path="/savings" element={<SaveAccount sessionCookie={sessionCookie} />} />
        <Route path="/card" element={<Card sessionCookie={sessionCookie} />} />

        <Route path="/applyAccount" element={<ApplyAccount sessionCookie={sessionCookie}/>} />
        <Route path="/applyCard" element={<ApplyCard sessionCookie={sessionCookie}/>} />

        <Route path='/accountAdd' element={<DepositSimulation sessionCookie={sessionCookie}/>} />
        <Route path="/applySaving" element={<ApplySaving sessionCookie={sessionCookie}/>} />

      </Routes>
    </BrowserRouter>
  );
}

//홈화면
function Home(){
  return(
  <div>
    <BannerComponent/>
  </div>)
}

//입금
const DepositSimulation = ({sessionCookie}) => {
  const [account_id, setAccountId] = useState('');
  const [money, setMoney] = useState('');

  const [showModal, setShowModal] = useState(false);

  const handleDeposit = async () => {
    try {
      const response = await axios.get('http://localhost:8080/back_bank/depositAccounts.do', {
        params: {
          account_id: account_id,
          money: money,
          JSESSIONID: sessionCookie
        }
      });
      // handle response data or set state as needed

      setShowModal(true);
    } catch (error) {
      console.error('Error making the deposit', error);
    }
  };

  return (
    <div className='center'>
    <div className="deposit-container">
      <input
        type="text"
        placeholder="Account ID"
        value={account_id}
        onChange={e => setAccountId(e.target.value)}
        className="input-field"
      />
      <input
        type="number"
        placeholder="Money"
        value={money}
        onChange={e => setMoney(e.target.value)}
        className="input-field"
      />
      <button onClick={handleDeposit} className="submit-button">입금</button>
    </div>
    {showModal && <ConfirmModal message="입금이 완료되었습니다." onClose={() => setShowModal(false)} />}
    </div>
  );
};

//확인 메세지 모델
const ConfirmModal = ({ message, onClose }) => {
  return (
    <div className="confirm-modal-container">
      <div className="confirm-modal-content">
        <p>{message}</p>
        <button onClick={onClose} className="confirm-button">확인</button>
      </div>
    </div>
  );
};

//로드 페이지 전환시
function loading(redi){

}

function Join(){
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [showModal, setShowModal] = useState(false);

  const handleLogin = async () => {

    try {
        const response = await axios.get('http://localhost:8080/back_bank/join.do',{
          params:{
            username: username,
            password: password
          }
        });

        if (response.status === 200) {
            setShowModal(true)
            setMessage('회원가입 성공 성공');
        } else {
          setShowModal(true)
          setMessage('회원가입 실패');
        }
    } catch (error) {
      setShowModal(true)  
      setMessage('회원가입 실패');
    }
  };

  return (
    <div className='center'>
      <div className='deposit-container'>
          <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="input-field"
          />
          <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="input-field"
          />
          <button className="submit-button" onClick={handleLogin}>회원가입</button>
      </div>

      {showModal && <ConfirmModal message={message} onClose={() => setShowModal(false)} />}
    </div>
  )
}

function accountAdd(){
  return 
}

function Login({setSessionCookie, setID}) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const [showModal, setShowModal] = useState(false);

  const handleLogin = async () => {

    try {
        const response = await axios.get('http://localhost:8080/back_bank/login', {
          params:{
            username: username,
            password: password
          }
        });

        console.log(response)

        if (response.status === 200) {

            document.cookie = `JSESSIONID=${response.data}}; path=/back_bank`;
            setSessionCookie(response.data);
            setMessage(response.data);
            setShowModal(true);

            setID(username)
        } else {
            setMessage('로그인 실패');
            setShowModal(true);
        }
    } catch (error) {
        setMessage('로그인 실패');
        setShowModal(true);
    }
  };

  return (
    <div className='center'>
      <div className='deposit-container'>
          <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="input-field"
          />
          <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="input-field"
          />
          <button className="submit-button" onClick={handleLogin}>로그인</button>
          {/* <button onClick={handleProtectedResource}>보호된 리소스 요청</button> */}
          <p>{message}</p>

        {showModal && <ConfirmModal message={message} onClose={() => setShowModal(false)} />}
      </div>
    </div>
);
}

function Menu(){
  const [menu, setMenu] = useState({
    "조회": "/account",
    "이체": "/transfer",
    "예금": "/savings",
    "신청": "/applyAccount"
  });

  const [child, setChild] = useState({
    "/account": {"카드조회":"/card", "계좌조회":"/account"},
    "/transfer":{"계좌이체":"/account", "입금": "/accountAdd"},
    "/savings": {"예금통장": "/savings"},
    "/applyAccount": {"계좌신청": "/applyAccount", "카드신청": "/applyCard"}
  })

  //css 사용해서 menuBox a { display: block;}
  const menuList = Object.keys(menu).map(x => 
    <MenuBox path={menu[x]} childs={child[menu[x]]} menuname={x} key={menu[x]}/>
  )
  return (
  <div className='menuContainer'>
    <ul className='menu'>
      {menuList}
    </ul>
  </div>)

}

//예금 기능 구현
function ApplySaving({sessionCookie}){

}



function MenuBox({path, childs, menuname}){
  const [showChildMenu, setShowChildMenu] = useState(false);

  const handleMouseEnter = () => {
    setShowChildMenu(true);
  };

  const handleMouseLeave = () => {
    setShowChildMenu(false);
  };

  //부모메뉴바
  return (
    <li className='MenuBox'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Link to={path}>{menuname}</Link>
      <ChidMenu
        path={path}
        childList={childs}
        show={showChildMenu}
      />
    </li>)
}

//메뉴 부분 컴포넌트

//계좌조회
function View({sessionCookie}){
  const [account, setAccount] = useState([
      {
        number: "00000000000",
        money: 500000,
        history: [{"11111": "+50000"}]
      },
      {
        number: "2836819827983",
        money: 500000,
        history: [{"12233": "+50000"}, {"13246" : "-3200"}]
      },
  ])

  useEffect(() => {
    ///getAccountsByUserAndType.do
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/back_bank/getAccountsByUserAndType.do', {
          params: {
            accountType: "inout",
            JSESSIONID: sessionCookie
          }
        });
        const res = [];
        for (const account of response.data) {
          const history = await axios.get('http://localhost:8080/back_bank/getTransactionHistory.do', {
            params: {
              accountId: account["number"],
              JSESSIONID: sessionCookie
            }
          });


          res.push({
            number: account["number"],
            money: account["money"],
            history: history.data
          });
        }

        setAccount(res);
      } catch (error) {
        console.error("API 요청 중 오류 발생:", error);
      }
    };

    fetchData();

  }, [])

  const accountList = account.map(obj=>
    <Account_Block history={obj.history} monney={obj.money} number={obj.number} key={obj.number}/>
  )
  return(
  <div className='container'>
    {accountList}
  </div>)
}

function Transfer({sessionCookie }) {

  const [accountIds, setAccountIds] = useState([]);
  const [selectedAccountId, setSelectedAccountId] = useState("");
  const [receiverAccountId, setReceiverAccountId] = useState("");
  const [amount, setAmount] = useState(0);
  const [balance, setBalance] = useState(null);

  const [message, setMessage] = useState("");
  const [showModal, setShowModal] = useState(false);


  const fetchData = async () => {

    const response = await axios.get('http://localhost:8080/back_bank/getAccountsByUserAndType.do', {
        params: {
          accountType: "inout",
          JSESSIONID: sessionCookie
        }
      });

    const ids = [];
    for (const account of response.data){
      ids.push(account["number"])
    }
    setAccountIds(ids)
    setSelectedAccountId(ids[0]);
  };

  const handleTransfer = async () => {
    try {
      const response = await axios.get('http://localhost:8080/back_bank/transferMoney.do', {
        params:{
          fromAccountId: selectedAccountId,
          toAccountId: receiverAccountId,
          amount: amount,
          JSESSIONID: sessionCookie
        }
      });
      if(response.status == 200) {
        setMessage("이체완료")
        setShowModal(true);
      } else {
        setShowModal(true);
        setMessage("이체실패" + response.data.message)
      }
    } catch (error) {
      setShowModal(true);
      setMessage('서버 에러: ' + error.message);
    }
  };

  const fetchBalance = async () => {
    try {
      console.log("현재 선택:", selectedAccountId)
      // 임시로 사용하는 API URL입니다. 실제 URL로 변경해주세요.
      const response = await axios.get('http://localhost:8080/back_bank/getAccountBalance.do',
      {
        params: {
          accountId: selectedAccountId,
          JSESSIONID: sessionCookie
        }
      });

      setBalance(response.data);
    } catch (error) {
      alert('잔액 조회 실패: ' + error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {

    if(selectedAccountId != ""){
      fetchBalance();
    }
  }, [selectedAccountId]);

  return (
    <div className='center'>
    <div className='deposit-container '>

      <div className='selectdiv'>
        <label>계좌 선택 </label>
        <select value={selectedAccountId} onChange={e => setSelectedAccountId(e.target.value)}>
          {accountIds.map(id => <option key={id} value={id}>{id}</option>)}
        </select>
      </div>

      <p>계좌 잔액: {balance}</p>

      <div>
        <input 
          type="text" 
          placeholder="이체할 계좌 번호" 
          value={receiverAccountId} 
          onChange={e => setReceiverAccountId(e.target.value)} 
        />
      </div>

      <div>
        <input 
          type="number" 
          placeholder="이체 금액" 
          value={amount} 
          onChange={e => setAmount(e.target.value)}
        />
      </div>

      <button className='submit-button' onClick={handleTransfer}>이체</button>


      {showModal && <ConfirmModal message={message} onClose={() => setShowModal(false)} />}

    </div>
    </div>
  );
}



function Loan(){
  return(
  <div>
    대출화면
  </div>)
}

function ApplyAccount({ sessionCookie }) {
  const [accountType, setAccountType] = useState('inout');  // 초기값은 입출금


  const [showModal, setShowModal] = useState(false);

  const applyAccount = async () => {
    //const endpoint = `http://localhost:8080/back_bank/createAccount.do?s=web&accountType=${accountType}`;
      //document.cookie = `JSESSIONID=${sessionCookie}}; path=/back_bank`;
      const response = await axios.get('http://localhost:8080/back_bank/createAccount.do', {
        params:{
          accountType: accountType,
          JSESSIONID: sessionCookie
        }
      });
  };

  const apply_dAccount = async () => {
    const response = await axios.get('http://localhost:8080/back_bank/applyDepositAccounts.do', {
        params:{
          JSESSIONID: sessionCookie
        }
      })
  }
  const handleSubmission = () => {

    try {
      if(accountType == "inout"){
        applyAccount()
      }
      else if(accountType == "save"){
        apply_dAccount();
      }
      setShowModal(true);
    } catch (error) {
      console.error('There was a problem with the request:', error.message);
    }
  }

  return (
    <div className='center'>
      <div className="selectdiv">
        <p className='title'>계좌종류 선택</p>
        <select
          id="accountType"
          value={accountType}
          onChange={(e) => setAccountType(e.target.value)}
        >
          <option value="inout">입출금</option>
          <option value="save">예금</option>
        </select>
        <button onClick={handleSubmission} className='button'>신청</button>

        {showModal && <ConfirmModal message="신청이 완료되었습니다" onClose={() => setShowModal(false)} />}
      </div>
    </div>

  );
}

function ApplyCard({ sessionCookie }) {
  const [cardType, setCardType] = useState('credit');  // 초기값은 입출금

  const [accountNumber, setAccountNumber] = useState("")


  const [showModal, setShowModal] = useState(false);

  const applyCard = async () => {
      const response = await axios.get('http://localhost:8080/back_bank/createCard.do', {
        params:{
          cardType:cardType,
          accountId:accountNumber,
          JSESSIONID: sessionCookie
        }
      });
  };

  const handleSubmission = () => {

    try {
        applyCard()
      setShowModal(true);
    } catch (error) {
      console.error('There was a problem with the request:', error.message);
    }
  }

  return (
    <div className='center'>
      <div className="selectdiv">
        <p className='title'>카드종류 선택</p>
        <select
          id="cardType"
          value={cardType}
          onChange={(e) => setCardType(e.target.value)}
        >
          <option value="credit">신용</option>
          <option value="check">체크</option>
        </select>

        <input
              type="text"
              placeholder="account number"
              value={accountNumber}
              onChange={(e) => setAccountNumber(e.target.value)}
              className="input-field"
        />

        <button onClick={handleSubmission} className='button'>신청</button>

        {showModal && <ConfirmModal message="신청이 완료되었습니다" onClose={() => setShowModal(false)} />}
      </div>
    </div>

  );
}

function Card({ sessionCookie}) {
  const [cards, setCards] = useState([])
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentCardType, setCurrentCardType] = useState(null);
  const [currentCardImage, setCurrentCardImage] = useState(null);

  useEffect(() => {
    const fetchCards = async (cardNumber) => {
      const response = await axios.get('http://localhost:8080/back_bank/viewCard.do', {
        params:{
          JSESSIONID: sessionCookie
        }
      });

      console.log(response.data)

      setCards(response.data);
    };

    fetchCards()

    if(cards.length > 0){
      if(cards[currentIndex]['ctype'] == "credit"){
        setCurrentCardImage("https://i.ibb.co/wrG7SLD/Kakao-Talk-20230827-195019490.jpg")
      }else{
        setCurrentCardImage("https://i.ibb.co/JQ3hn3f/Kakao-Talk-20230827-195023926.jpg")
      }
    }
  }, [])

  useEffect(() => {
    if(cards.length > 0){
      if(cards[currentIndex]['ctype'] == "credit"){
        setCurrentCardImage("https://i.ibb.co/wrG7SLD/Kakao-Talk-20230827-195019490.jpg")
      }else{
        setCurrentCardImage("https://i.ibb.co/JQ3hn3f/Kakao-Talk-20230827-195023926.jpg")
      }
    }

  }, [currentIndex, currentCardType]);

  return (
    <div className="card-container">
      {currentCardImage && cards[currentIndex].cid }
      {currentCardImage && <img src={currentCardImage} alt="Card"/>}
      <div>
        <button onClick={() => setCurrentIndex(prev => (prev > 0 ? prev - 1 : cards.length - 1))}>이전</button>
        <button onClick={() => setCurrentIndex(prev => (prev < cards.length - 1 ? prev + 1 : 0))}>다음</button>
      </div>
    </div>
  );
}

//예금
function SaveAccount({sessionCookie}){
  const [Account, setAccount] = useState({
  })


  const getDepAccountList = async () => {
    const response = await axios.get('http://localhost:8080/back_bank/viewDepositAccounts.do', {
        params:{
          JSESSIONID: sessionCookie
        }
    })

    for(let data of response.data){
      setAccount(prevAccount => ({
        ...prevAccount,
        [data.a_id]:{
          "money": data.money,
          "date" : data.date,
        }
      }));

    }
  }

  useEffect(() =>{
    //불러오기
    getDepAccountList()
  }, [])

  const Accounts = () => Object.keys(Account).map(e => {

    return <SaveAccountBlock number={e} endDate={Account[e].date} money={Account[e].money}/>
  })

  return(<div className='container'>
    {Accounts()}
  </div>)
}

function SaveAccountBlock({number, endDate, money}){
  const [showModal, setShowModal] = useState(false);

  const handleSubmission = () => {
    setShowModal(true);
  }
  return (<div className='containerBox'>
      계좌번호 {number}<br/>
      금액 {money}원<br/>
      만기일 {endDate}<br/>
      <input type="text" name="acccount_id"/>
      <button onClick={handleSubmission} >확인</button>
      {showModal && <ConfirmModal message="해지기간이 아닙니다" onClose={() => setShowModal(false)} />}
  </div>)
}

//자식 메뉴
function ChidMenu({path, childList, show}){
  const [displayCss, setDisplayCss] = useState({display: 'none'})

  const menuList = Object.keys(childList).map(x => 
    <li className="childMenuBox" key={`${path}${childList[x]}`}>
      <Link to={`${childList[x]}`}>{x}</Link>
    </li>
  )

  useEffect(()=>{
    if(show){
      setDisplayCss({display: 'block'})
    }
    else{
      setDisplayCss({display: 'none'})
    }

  },[show])

  return (
    <div className='ChildMenu' style={displayCss}>
      <ul>
        {menuList}
      </ul>
    </div>)
}

//계좌 블럭
//account block
function Account_Block({number, history, monney}){
  const [showHistory, setShowHistory] = useState(false);

  const handleMouseEnter = () => {
    setShowHistory(true);
  };

  const handleMouseLeave = () => {
    setShowHistory(false);
  };

  return(
  <div className='containerBox'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
  >
    <a className='accountName'>계좌번호 {number}</a>
    <a className='accountMoney'> {monney}원 </a>
    <Account_history history={history} showHistory={showHistory}/>

  </div>)
}

function Account_history({history, showHistory}){
  const historyList = history.map(obj=>
    <li className='historyBox'>{Object.keys(obj)[0]} {obj[Object.keys(obj)[0]]}원</li>
    // <li className='historyBox'>{x}</li>
  )
  const [css, setCss] = useState(
    {
      overflow: 'auto',
      width: "100%",
      height: "0px",
      transition: 'width 0.5s ease-out, height 0.5s ease-out'
    }
  )
  useEffect(()=>{
    if(showHistory){
      setCss({
        ...css,
        width: "100%",
        height: "200px",
      })
    }else{
      setCss({
        ...css,
        width: "100%",
        height: "0px",
      })
    }
  },[showHistory])


  return(
  <div className='accountHistory' style={css}>
    <ul> {historyList} </ul>
  </div>)
}

function BannerComponent() {
  const [currentImageIndex, setCurrentImageIndex] = useState(1);

  const images = [
    "https://i.ibb.co/2YTNxCj/2023-08-28-012130.png",
    "https://i.ibb.co/QdvK7w2/Kakao-Talk-20230827-193323521.jpg",
    "https://i.ibb.co/YhfPm8p/Kakao-Talk-20230827-193326823.jpg"
    // ... 기타 이미지 URL들
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000); 

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="banner-container">
      {images.map((image, index) => (
        <img
          key={index}
          src={image}
          alt={`배너 이미지 ${index + 1}`}
          className={`banner-image ${currentImageIndex === index ? 'active' : ''}`}
        />
      ))}
    </div>
  );
}

export default App;
