import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Link, Routes } from 'react-router-dom';

function App() {
  return (
    <BrowserRouter>
      <nav>
        <div>
          <Link to="/">Home</Link> 
        </div>
        <Menu/>
        <div>
          <Link to="/login">로그인</Link>
        </div>
      </nav>
      <main>
        메인 화면
      </main>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/view" element={<View />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </BrowserRouter>
  );
}

//로드 페이지 전환시
function loading(){

}

function View(){
  return(
  <div>
    조회화면
  </div>)
}

function Login() {
  return (
    <div>
      <form className='LoginForm' action="">
        ID: <input type='text'></input>
        PassWord <input type='text'></input>
        <button>로그인</button>
      </form>
    </div>
  );
}

function Menu(){
  const [menu, setMenu] = {
    "조회" : "/view",
    "이체" : "",
    "적금" : "", 
    "대출" : "", 
    "카드" : ""
  }
  //css 사용해서 menuBox a { display: block;}
  const menuList = Object.keys(menu).map(x => 
    <li className='menuBox'>
      <Link to={menu.x}>{x}</Link>
    </li>
  )

  return (
  <div className='menu'>
    <ul>
      {menuList}
    </ul>
  </div>)
}

//홈화면
function Home(){
  return(
  <div href="">
    Bank
  </div>)
}

//배너
function Banner(){
  const [content, setContent] = {}

  useEffect(()=>{
    //배너 목록을 가져오는 api
    fetch("")
      .then(data=>{
        setContent(JSON.parse(data))
      })
  }, [content])
}

export default App;
