import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Link, Routes } from 'react-router-dom';
import './App.css';

function App() {
  return (
    <BrowserRouter>
      <nav>
        <div>
          <Link to="/">Home</Link> 
        </div>
        <Menu/>
        <div>
          <Link to="/login">로그인</Link>
        </div>
      </nav>
      <main>
        메인 화면
      </main>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />

        <Route path="/account" element={<View />} />
        <Route path="/transfer" element={<Transfer />} />
        <Route path="/savings" element={<Savings />} />
        <Route path="/loan" element={<Loan />} />
        <Route path="/card" element={<Card />} />
      </Routes>
    </BrowserRouter>
  );
}

//홈화면
function Home(){
  return(
  <div>
    배너, 상품 목록
  </div>)
}

//로드 페이지 전환시
function loading(redi){

}

function Login() {
  return (
    <div>
      <form className='LoginForm' action="">
        ID: <input type='text'></input>
        PassWord <input type='text'></input>
        <button>로그인</button>
      </form>
    </div>
  );
}

function Menu(){
  const [menu, setMenu] = useState({
    "계좌": "/account",
    "이체": "/transfer",
    "적금": "/savings",
    "대출": "/loan",
    "카드": "/card"
  });

  const [child, setChild] = useState({
    "/view": {"카드조회":"/card", "계좌조회":"/account"},
    "/transfer":{"계좌이체":"/account"},
    "/savings": {"적금계좌":"/account"},
    "/loan": {"신용대출": "/basic"},
    "/card": {"체크카드":"/check", "신용카드":"/credit"}
  })

  //css 사용해서 menuBox a { display: block;}
  const menuList = Object.keys(menu).map(x => 
    <MenuBox path={menu[x]} childs={child[menu[x]]} menuname={x}/>
  )
  return (
  <div className='menu'>
    <ul>
      {menuList}
    </ul>
  </div>)
}

function MenuBox({path, childs, menuname}){
  const [showChildMenu, setShowChildMenu] = useState(false);

  const handleMouseEnter = () => {
    setShowChildMenu(true);
  };

  const handleMouseLeave = () => {
    setShowChildMenu(false);
  };

  return (
    <li className='MenuBox'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Link to={path}>{menuname}</Link>
      <ChidMenu
        path={path}
        childList={childs}
        show={showChildMenu}
      />
    </li>)
}

//메뉴 부분 컴포넌트
function View(){
  return(
  <div>
    조회화면
  </div>)
}

function Transfer(){
  return(
  <div>
    이체화면
  </div>)
}

function Savings(){
  return(
  <div>
    적금화면
  </div>)
}

function Loan(){
  return(
  <div>
    대출화면
  </div>)
}

function Card(){
  return(
  <div>
    카드화면
  </div>)
}

//하위 메뉴
function ChidMenu({path, childList, show}){
  const [displayCss, setDisplayCss] = useState({display: 'none'})

  const menuList = Object.keys(childList).map(x => 
    <li className='MenuBox'>
      <Link to={`${path}${childList[x]}`}>{x}</Link>
    </li>
  )

  useEffect(()=>{
    if(show){
      setDisplayCss({display: 'block'})
    }
    else{
      setDisplayCss({display: 'none'})
    }

  },[show])

  return (
    <div className='ChildMenu' style={displayCss}>
      <ul>
        {menuList}
      </ul>
    </div>)
}

//배너
function Banner(){
  const [content, setContent] = {}

  useEffect(()=>{
    //배너 목록을 가져오는 api
    fetch("")
      .then(data=>{
        setContent(JSON.parse(data))
      })
  }, [content])
}

export default App;
