function Card({ Cards }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentCardType, setCurrentCardType] = useState(null);
  const [currentCardImage, setCurrentCardImage] = useState(null);

  useEffect(() => {
    const fetchCardType = async (cardNumber) => {
      try {
        const response = await axios.get(`http://your-api-url.com/cardtype?cardNumber=${cardNumber}`);
        setCurrentCardType(response.data.type);
      } catch (error) {
        console.error('Error fetching card type:', error);
      }
    };

    const fetchCardImage = async (cardType) => {
      try {
        const response = await axios.get(`http://your-api-url.com/cardimage?type=${cardType}`);
        setCurrentCardImage(response.data.imageUrl);
      } catch (error) {
        console.error('Error fetching card image:', error);
      }
    };

    if (Cards[currentIndex]) {
      fetchCardType(Cards[currentIndex]).then(() => {
        if (currentCardType) {
          fetchCardImage(currentCardType);
        }
      });
    }

  }, [currentIndex, currentCardType]);

  return (
    <div>
      <h2>카드화면</h2>
      {currentCardImage && <img src={currentCardImage} alt="Card" />}
      <div>
        <button onClick={() => setCurrentIndex(prev => (prev > 0 ? prev - 1 : Cards.length - 1))}>이전</button>
        <button onClick={() => setCurrentIndex(prev => (prev < Cards.length - 1 ? prev + 1 : 0))}>다음</button>
      </div>
    </div>
  );
}

