import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Link, Routes } from 'react-router-dom';
import './App.css';
import axios from 'axios';


function App() {
  //로그인 쿠키 저장예정
  const [sessionCookie, setSessionCookie] = useState(null)
  const [accountId, setAccountId] = useState({})
  const [card, setCard] = useState({})

  return (
    <BrowserRouter>
      <nav>
        <div className='home'>
          <Link to="/">Capstone Bank</Link> 
        </div>
        <Menu/>
        <div className='loginButton'>
          <Link to="/login">로그인</Link>
        </div>
      </nav>
      <main>
      </main>
      <Routes>
        <Route path="/" element={<Home cookie={sessionCookie}/>} />
        <Route path="/login" element={<Login />} />

        <Route path="/account" element={<View cookie={sessionCookie}/>}/>
        <Route path="/transfer" element={<Transfer accountId={accountId} />} />
        <Route path="/savings" element={<Savings />} />
        <Route path="/loan" element={<Loan />} />
        <Route path="/card" element={<Card Cards={card} />} />
      </Routes>
    </BrowserRouter>
  );
}

//홈화면
function Home(){
  return(
  <div>
    배너, 상품 목록
  </div>)
}

//로드 페이지 전환시
function loading(redi){

}

function Login() {
  return (
    <div>
      <form className='LoginForm' action="">
        ID: <input type='text'></input>
        PassWord <input type='text'></input>
        <button>로그인</button>
      </form>
    </div>
  );
}

function Menu(){
  const [menu, setMenu] = useState({
    "계좌": "/account",
    "이체": "/transfer",
    "예금": "/savings",
    "대출": "/loan",
    "카드": "/card"
  });

  const [child, setChild] = useState({
    "/account": {"카드조회":"/card", "계좌조회":"/account"},
    "/transfer":{"계좌이체":"/account"},
    "/savings": {"예금통장": "/account", "예금신청":"/req"},
    "/loan": {"신용대출": "/basic"},
    "/card": {"체크카드":"/check", "신용카드":"/credit"}
  })

  //css 사용해서 menuBox a { display: block;}
  const menuList = Object.keys(menu).map(x => 
    <MenuBox path={menu[x]} childs={child[menu[x]]} menuname={x} key={menu[x]}/>
  )
  return (
  <div className='menuContainer'>
    <ul className='menu'>
      {menuList}
    </ul>
  </div>)

}

function SaveAccount(){
  const [Account, setAccount] = useState({
    "계좌번호" : {
      "money": 1000,
      "date" : "2023 7월",
    }


  })
}

function MenuBox({path, childs, menuname}){
  const [showChildMenu, setShowChildMenu] = useState(false);

  const handleMouseEnter = () => {
    setShowChildMenu(true);
  };

  const handleMouseLeave = () => {
    setShowChildMenu(false);
  };

  //부모메뉴바
  return (
    <li className='MenuBox'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Link to={path}>{menuname}</Link>
      <ChidMenu
        path={path}
        childList={childs}
        show={showChildMenu}
      />
    </li>)
}

//메뉴 부분 컴포넌트

//계좌조회
function View({cookie}){
  const [account, setAccount] = useState([
      {
        number: "00000000000",
        money: 500000,
        history: {"11111": "+50000"}
      },
      {
        number: "2836819827983",
        money: 500000,
        history: {"12233": "+50000", "13246" : "-3200"}
      },
  ])

  const accountList = account.map(obj=>
    <Account_Block history={obj.history} monney={obj.money} number={obj.number} key={obj.number}/>
  )
  return(
  <div className='accountCotainer'>
    {accountList}
  </div>)
}

function Transfer({ accountIds }) {
  const [selectedAccountId, setSelectedAccountId] = useState(accountIds[0] || "");
  const [receiverAccountId, setReceiverAccountId] = useState("");
  const [amount, setAmount] = useState(0);
  const [balance, setBalance] = useState(null);

  const handleTransfer = async () => {
    try {
      const response = await axios.post('http://localhost:3000/transfer', {
        fromAccountId: selectedAccountId,
        toAccountId: receiverAccountId,
        amount
      });
      if(response.data.success) {
        alert('이체 완료!');
      } else {
        alert('이체 실패: ' + response.data.message);
      }
    } catch (error) {
      alert('서버 에러: ' + error.message);
    }
  };

  const fetchBalance = async () => {
    try {
      // 임시로 사용하는 API URL입니다. 실제 URL로 변경해주세요.
      const response = await axios.get('http://your-api-url.com/balance?accountId=' + selectedAccountId);
      setBalance(response.data.balance);
    } catch (error) {
      alert('잔액 조회 실패: ' + error.message);
    }
  };

  useEffect(() => {
    fetchBalance();
  }, [selectedAccountId]);

  return (
    <div>
      <h2>이체화면</h2>

      <div>
        <label>계좌 선택: </label>
        <select value={selectedAccountId} onChange={e => setSelectedAccountId(e.target.value)}>
          {accountIds.map(id => <option key={id} value={id}>{id}</option>)}
        </select>
      </div>

      <p>계좌 잔액: {balance}</p>

      <div>
        <input 
          type="text" 
          placeholder="이체할 계좌 번호" 
          value={receiverAccountId} 
          onChange={e => setReceiverAccountId(e.target.value)} 
        />
      </div>

      <div>
        <input 
          type="number" 
          placeholder="이체 금액" 
          value={amount} 
          onChange={e => setAmount(Number(e.target.value))}
        />
      </div>

      <button onClick={handleTransfer}>이체</button>
      <button onClick={fetchBalance}>잔액 조회</button>
    </div>
  );
}

function Savings(){
  return(
  <div>
    적금화면
  </div>)
}

function Loan(){
  return(
  <div>
    대출화면
  </div>)
}


function Card({ Cards }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentCardType, setCurrentCardType] = useState(null);
  const [currentCardImage, setCurrentCardImage] = useState(null);

  useEffect(() => {
    const fetchCardType = async (cardNumber) => {
      try {
        const response = await axios.get(`http://your-api-url.com/cardtype?cardNumber=${cardNumber}`);
        setCurrentCardType(response.data.type);
      } catch (error) {
        console.error('Error fetching card type:', error);
      }
    };

    const fetchCardImage = async (cardType) => {
      try {
        const response = await axios.get(`http://your-api-url.com/cardimage?type=${cardType}`);
        setCurrentCardImage(response.data.imageUrl);
      } catch (error) {
        console.error('Error fetching card image:', error);
      }
    };

    if (Cards[currentIndex]) {
      fetchCardType(Cards[currentIndex]).then(() => {
        if (currentCardType) {
          fetchCardImage(currentCardType);
        }
      });
    }

  }, [currentIndex, currentCardType]);

  return (
    <div>
      <h2>카드화면</h2>
      {currentCardImage && <img src={currentCardImage} alt="Card" />}
      <div>
        <button onClick={() => setCurrentIndex(prev => (prev > 0 ? prev - 1 : Cards.length - 1))}>이전</button>
        <button onClick={() => setCurrentIndex(prev => (prev < Cards.length - 1 ? prev + 1 : 0))}>다음</button>
      </div>
    </div>
  );
}

//자식 메뉴
function ChidMenu({path, childList, show}){
  const [displayCss, setDisplayCss] = useState({display: 'none'})

  const menuList = Object.keys(childList).map(x => 
    <li className="childMenuBox" key={`${path}${childList[x]}`}>
      <Link to={`${path}${childList[x]}`}>{x}</Link>
    </li>
  )

  useEffect(()=>{
    if(show){
      setDisplayCss({display: 'block'})
    }
    else{
      setDisplayCss({display: 'none'})
    }

  },[show])

  return (
    <div className='ChildMenu' style={displayCss}>
      <ul>
        {menuList}
      </ul>
    </div>)
}

//계좌 블럭
//account block
function Account_Block({number, history, monney}){
  const [showHistory, setShowHistory] = useState(false);

  const handleMouseEnter = () => {
    setShowHistory(true);
  };

  const handleMouseLeave = () => {
    setShowHistory(false);
  };

  return(
  <div className='accountBlock'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
  >
    <a className='accountName'>계좌번호 {number}</a>
    <a className='accountMoney'> {monney} </a>
    <Account_history history={history} showHistory={showHistory}/>

  </div>)
}

function Account_history({history, showHistory}){

  const historyList = Object.keys(history).map(x=>
    <li className='historyBox'>{x} {history[x]}원</li>
  )
  const [css, setCss] = useState(
    {
      overflow: 'auto',
      width: "100%",
      height: "0px",
      transition: 'width 0.5s ease-out, height 0.5s ease-out'
    }
  )
  useEffect(()=>{
    if(showHistory){
      setCss({
        ...css,
        width: "100%",
        height: "200px",
      })
    }else{
      setCss({
        ...css,
        width: "100%",
        height: "0px",
      })
    }
  },[showHistory])


  return(
  <div className='accountHistory' style={css}>
    <ul> {historyList} </ul>
  </div>)
}

//배너
function Banner(){
  const [content, setContent] = {}

  useEffect(()=>{
    //배너 목록을 가져오는 api
    fetch("")
      .then(data=>{
        setContent(JSON.parse(data))
      })
  }, [content])

  return(<div> 배너화면 </div>)
}

export default App;
